package decisionMaking;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x =1;
		switch(x){
			
		case 1:
			System.out.println("1");		
			break;
		case 2:
			System.out.println("2");		
			break;
		
		default:
			System.out.println("i dont know");		
			break;
		
		}

	}

}
